package com.example.fragmentmanager;import android.os.Parcel;import android.os.Parcelable;public class Bean implements Parcelable {    private String name;    public Bean(String name) {        this.name = name;    }    protected Bean(Parcel in) {        name = in.readString();    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @Override    public String toString() {        return "Bean{" +                "name='" + name + '\'' +                '}';    }    public static final Creator<Bean> CREATOR = new Creator<Bean>() {        @Override        public Bean createFromParcel(Parcel in) {            return new Bean(in);        }        @Override        public Bean[] newArray(int size) {            return new Bean[size];        }    };    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel parcel, int i) {        parcel.writeString(name);    }}