package com.example.mrecycleview;import android.content.Context;import android.os.Handler;import android.os.Message;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.List;public class MAdapter extends RecyclerView.Adapter<MAdapter.MAdapterHolder> {    private Context mContext;    private List<Bean> mList;    public MAdapter(Context mContext, List<Bean> mList) {        this.mContext = mContext;        this.mList = mList;    }    /*step1：获取itemView返回返回给holder*/    @NonNull    @Override    public MAdapterHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = View.inflate(mContext, R.layout.layout_item, null);        return new MAdapterHolder(view);    }    /*step3：给UI赋值*/    @Override    public void onBindViewHolder(@NonNull MAdapterHolder holder, int position) {        holder.tv.setText(mList.get(position).getName());    }    @Override    public int getItemCount() {        return mList == null ? 0 : mList.size();    }    /*step4：给外界暴露一个itemView点击事件的postion*/    public void setOnRecyclerClickerListener( OnRecyclerClickListener listener) {        this.recyclerClickListener = listener;    }    private OnRecyclerClickListener recyclerClickListener;    /*创建一个回调接口*/    interface OnRecyclerClickListener{        void OnRecyclerItemClick(int postion);    }    /*step2：实例化ui*/    public class MAdapterHolder extends RecyclerView.ViewHolder {        private TextView tv;        public MAdapterHolder(@NonNull View itemView) {            super(itemView);            tv = itemView.findViewById(R.id.tv_name);/*实例化itemlist中的UI*/            itemView.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View view) {                    /*往接口中添加参数，当界面重写这个接口的时候可以获取到onClick方法传入的postion*/                    recyclerClickListener.OnRecyclerItemClick(getAdapterPosition());                }            });        }    }}