package com.example.viewpage;import android.content.Context;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.viewpager.widget.PagerAdapter;import java.util.List;public class MAdapter extends PagerAdapter {    private List<View> list;    public MAdapter(List<View> list) {        this.list = list;    }    @Override    public int getCount() {        return list == null ? 0 : list.size();    }    @NonNull    @Override    public Object instantiateItem(@NonNull ViewGroup container, int position) {        container.addView(list.get(position), 0);        return list.get(position);    }    @Override    public boolean isViewFromObject(@NonNull View view, @NonNull Object object) {        return view == object;    }    @Override    public void destroyItem(@NonNull ViewGroup container, int position, @NonNull Object object) {        container.removeView(list.get(position));    }}